oxy-read-timeout: &quot;120&quot; nginx.ingress.kubernetes.io/proxy-send-timeout: &quot;120&quot;spec: rules: - host: api.customerservice.example.com http: paths: - path: /api/v1 pathType: Prefix backend: service: name: customer-service-api port: number: 80 ç›‘æ§ä¸å‘Šè­¦é…ç½®ï¼š1234567891011121314151617181920212223242526272829303132333435363738394041424344454647484950515253545556575859606162636465666768697071727374757677787980818283848586878889909192# monitoring.pyfrom prometheus_client import Counter, Histogram, Gauge, start_http_serverimport timeimport loggingfrom functools import wraps# å®šä¹‰ç›‘æ§æŒ‡æ ‡REQUEST_COUNT = Counter('customer_service_requests_total', 'Total customer service requests', ['endpoint', 'status_code'])RESPONSE_TIME = Histogram('customer_service_response_seconds', 'Response time in seconds', ['endpoint'])CONFIDENCE_SCORE = Histogram('customer_service_confidence', 'Response confidence scores', ['category'])GPU_UTILIZATION = Gauge('gpu_utilization_percent', 'GPU utilization percentage')MEMORY_USAGE = Gauge('memory_usage_gb', 'Memory usage in GB')ACTIVE_SESSIONS = Gauge('active_sessions', 'Number of active chat sessions')# è£…é¥°å™¨ç”¨äºè‡ªåŠ¨ç›‘æ§def monitor_endpoint(endpoint_name): def decorator(func): @wraps(func) def wrapper(*args, **kwargs): start_time = time.time() try: result = func(*args, **kwargs) status_code = 200 if result else 500 REQUEST_COUNT.labels(endpoint=endpoint_name, status_code=status_code).inc() # æå–ç½®ä¿¡åº¦ï¼ˆå¦‚æœå­˜åœ¨ï¼‰ if hasattr(result, 'confidence'): CONFIDENCE_SCORE.labels(category=getattr(result, 'category', 'unknown')).observe(result.confidence) return result except Exception as e: logging.error(f&quot;Endpoint {endpoint_name} failed: {str(e)}&quot;) REQUEST_COUNT.labels(endpoint=endpoint_name, status_code=500).inc() raise finally: RESPONSE_TIME.labels(endpoint=endpoint_name).observe(time.time() - start_time) return wrapper return decorator# GPUç›‘æ§çº¿ç¨‹def gpu_monitoring_thread(): import pynvml pynvml.nvmlIn